import React from 'react';
import { Image } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const ImageDisplay = ({ image, resize, draw }) => {
    return (
        <div className='d-flex justify-content-center align-items-center'>
            {image && <Image src={image} alt="imageUploaded" fluid className="mt-3" />}
            {resize && <Image src={resize} alt="resizeUploaded" fluid className="mt-3" />}
            {draw && <Image src={draw} alt="drawUploaded" fluid className="mt-3" />}
        </div>
    );
};

export default ImageDisplay;
