import React, { useState, useEffect } from 'react';
import { Form, Button, Alert, Container, Image } from 'react-bootstrap';
import ImageDisplay from './imageDisplay';
import ProgressBar from 'react-bootstrap/ProgressBar';
import axios from 'axios';


const FormSubmitted = ({image, resize, grid}) => {
    const [currentImage, setCurrentImage] = useState(null);
    const [probList, setProbList] = useState([]);
    const [pred, setPred] = useState(null);
    const [l2, setL2] = useState(null);
    const [gridImage, setGridImage] = useState(null);
    const [success, setSuccess] = useState(null)

    useEffect(() => {
        const eventSource = new EventSource('http://127.0.0.1:8000/attack_sse');

        eventSource.onmessage = (event) => {
            const data = JSON.parse(event.data);
            setCurrentImage(`data:image/png;base64,${data.attacked}`);
            setGridImage(`data:image/png;base64,${data.attacked_grid}`);
            setProbList(data.prob);
            setPred(data.pred);
            setL2(data.l2);
            setSuccess(data.success)
        };
        eventSource.onerror = () => {
            eventSource.close();
        };
        return () => {
            eventSource.close();
        };
    }, []);

    const handleAttack = async () => {
        try {
            const formData = new FormData();
            formData.append('file', image);

            const response = await axios.post('http://127.0.0.1:8000/attack', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log('Response from server:', response.data);
        } catch (error) {
            console.error('Error uploading image:', error);
        }
    };

    return (
        <Container className='d-flex p-4'>
            <div className=''>
                <div className='border p-2'>
                    <div className='d-flex justify-content-center align-items-center'>
                        {image && <Image src={image} alt="imageUploaded" fluid className="mt-3" />}
                        {resize && <Image src={resize} alt="imageUploaded" fluid className="mt-3" />}
                        <ImageDisplay resize={currentImage} draw={gridImage} />
                    </div>
                </div>
            </div>
            <div className='ml-3 p-2 flex-grow-1'>
                <div className='mb-3'>
                    {/* <div className="text-base font-medium">Class 1</div>
                    <ProgressBar variant='red-bar' now={prob_list[0]*100} label={`${prob_list[0]*100}%`} />
                    <div className="text-base font-medium">Class 2</div>
                    <ProgressBar variant='orange-bar' now={prob_list[1]*100} label={`${prob_list[1]*100}%`} />
                    <div className="text-base font-medium">Class 3</div>
                    <ProgressBar variant='yellow-bar' now={prob_list[2]*100} label={`${prob_list[2]*100}%`} />
                    <div className="text-base font-medium">Class 4</div>
                    <ProgressBar variant='green-bar' now={prob_list[3]*100} label={`${prob_list[3]*100}%`} />
                    <div className="text-base font-medium">Class 5</div>
                    <ProgressBar variant='blue-bar' now={prob_list[4]*100} label={`${prob_list[4]*100}%`} />
                    <div className="text-base font-medium">Class 6</div>
                    <ProgressBar variant='darkblue-bar' now={prob_list[5]*100} label={`${prob_list[5]*100}%`} />
                    <div className="text-base font-medium">Class 7</div>
                    <ProgressBar variant='purple-bar' now={prob_list[6]*100} label={`${prob_list[6]*100}%`} />
                    <div className="text-base font-medium">Class 8</div>
                    <ProgressBar variant='pink-bar' now={prob_list[7]*100} label={`${prob_list[7]*100}%`} />
                    <div className="text-base font-medium">Class 9</div>
                    <ProgressBar variant='cyan-bar' now={prob_list[8]*100} label={`${prob_list[8]*100}%`} />
                    <div className="text-base font-medium">Class 10</div>
                    <ProgressBar variant='choco-bar' now={prob_list[9]*100} label={`${prob_list[9]*100}%`} /> */}
                    {probList.map((prob, index) => (
                        <div key={index}>
                            <div className="text-base font-medium">Class {index + 1}</div>
                            <ProgressBar variant={`${index}-bar`} now={prob * 100} label={`${(prob * 100).toFixed(2)}%`} />
                        </div>
                    ))}
                </div>
                <Alert variant='success' className='mt-3'>Predict: {pred}</Alert>
                <Alert variant='primary' className='mt-3'>L2 Norm: {l2}</Alert>
                <div className='d-flex justify-content-center'>
                    <Button onClick={handleAttack} variant='danger' className='mt-3'>Attack</Button>
                </div>
            </div>
        </Container>
    )
}

export default FormSubmitted;
