import React, { useState } from 'react';
import { Container, Button } from 'react-bootstrap';
import UploadForm from './components/form';
import ImageDisplay from './components/imageDisplay';
import Attack from './components/attack'

function App() {
    const [prediction, setPrediction] = useState('');
    const [image, setImage] = useState(null);
    const [resize, setResize] = useState(null);
    const [draw, setDraw] = useState(null);
    const [isHidden, setIsHidden] = useState(true);

    const handleFormSubmit = () => {
        console.log('Button should be shown now.')
        setIsHidden(false); // Show the button after form submission
    };

    return (
        <Container className="mt-5">
            <h1 className="mb-4">Reinforcement Learning in Adversarial Attack with Classification Model</h1>
            <UploadForm 
                setPrediction={setPrediction} 
                setImage={setImage} 
                setResize={setResize} 
                setDraw={setDraw} 
                onFormSubmit={handleFormSubmit}
            />
            <Attack image={image} />
            <ImageDisplay image={image} resize={resize} draw={draw} />
            <div className='d-flex align-items-center justify-content-center' id='buttonAttack' style={{ display: isHidden ? 'none' : 'flex' }}>
                <Button variant='danger' className='mt-3'>Attack</Button>
            </div>
        </Container>
    );
}

export default App;
